import React, { Component } from 'react';
import {Link} from 'react-router-dom'

class Product extends Component {
    
    render() {
        const {title,image,price,inCart}=this.props.product;
        console.log(title)
        return (
            <div className="div1 card col-lg-3 mx-3 my-3 col-md-5 col-sm-10"
            onClick={()=> console.log('you clicked')} >
    <Link to='/detail'> <img src={image} className='img-container p-3' width="60%" alt='images' />
    </Link>
<button onClick={()=> console.log('added to cart')} className="cart-btn mb-0" disabled={inCart? true:false}>
{inCart?(<p className='text-capitalize'>in cart</p>):(<i className="fas fa-cart-plus"></i>)}</button>   

            

<div className="card-footer dflex">
<p className="mb-0">{title}</p>
<h5 className='font-italic mb-0'><span className='mr-1'>$</span>{price}</h5>
</div>
</div>
        );
    }
}
export default Product;
