import React, { Component } from 'react';
import {Consumerp} from '../context'
class Details extends Component {
    render() {
        return (
            <div>
                <Consumerp>
                    {value=>{
                    const {title,company,info,image,inCart}=value.detailproduct
              return(
                  
                  <div className='card my-3'>
                      <h3 className="title my-5 text-center">{title}</h3>
                       <div className='row'> <img className='col-5' src={image} alt={image}/>
                       <h3 className="price">Price: <span>${price}</span></h3>
                       <p className='col-5'>{info}</p>
                       </div>
                                     
                  </div>
              )
              }}
                </Consumerp>
            </div>
        );
    }
}

export default Details;
