import React, { Component } from 'react';
import {Link} from 'react-router-dom'

class Product extends Component {
    
    render() {
        const {title,image,price}=this.props.product;
        console.log(title)
        return (
            <div className="card col-lg-4 mx-3 my-3  col-sm-12">
    <Link to='/detail'> <img src={image}  width="60%" alt='images' />
    </Link>
           
           <h4><b>{title}</b></h4>
            <h4 style={{float:"right"}}>`${price}`</h4>

            </div>
        );
    }
}

export default Product;
