import React, { Component } from 'react';
import {storeProducts,detailProduct} from './data'

const Providerp=React.createContext();

class Provider extends Component {
    state={
        products:[],
        detailproduct:detailProduct
    }
    componentDidMount(){
        this.setproduct();
    }

    setproduct=()=>{
        let tempprpduct=[];
        storeProducts.forEach(item=>{
            const single={...item};
            tempprpduct=[...tempprpduct,single]
        });
        this.setState({products:tempprpduct})
    }

    addtocart(id){
        console.log(`in cart ${id}`)
    }
    handledetail(){
        console.log('detailspp')
    }
    render() {
        return (
            <div>
                <Providerp.Provider value={{...this.state,addtocart:this.addtocart,handledetail:this.handledetail}}>
                    {this.props.children}
                </Providerp.Provider>
            </div>
        );
    }
}

const Consumerp=Providerp.Consumer;

export  {Provider,Consumerp};
