import React from 'react';

const Carttotal = ({value}) => {
    const {total,tax,subtotal,clearcart}={value};
    return (
        <div className='row'>
        <div className="text-right ml-sm-auto ml-md-auto">
            <button className='btn btn-danger mb-3 b-0 r-0' onClick={()=>clearcart()}>Clear Cart</button>
           <div> <span>Subtotal: ${subtotal}</span></div>
            <span>Tax :${tax}</span>
            <span>Total :${total}</span>
            </div>
        </div>
    );
};

export default Carttotal;
