import React from 'react';

const Cartitem = ({value,item}) => {
    const {increment,decrement,remove,clearcart}=value;
    const {image,title,price,total,count}=item;
    return (
        <div>
            <div class='row'>
                <div className="col-lg-2 col-sm-10"><img src={image} alt={image}/></div>
                <div className="col-lg-2 col-sm-10"><span className="d-lg-none">Name:</span>{title}</div>
                <div className="col-lg-2 col-sm-10"><span className="d-lg-none">Product:</span></div>
                <div className="col-lg-2 col-sm-10"><span className="d-lg-none">Product:</span></div>
                <div className="col-lg-2 col-sm-10"><span className="d-lg-none">Product:</span></div>
                <div className="col-lg-2 col-sm-10"><span className="d-lg-none">Product:</span></div>
            </div>
        </div>
    );
};

export default Cartitem;
