import React, { Component } from 'react';
import {storeProducts,detailProduct} from './data'

const Providerp=React.createContext();

class Provider extends Component {
    state={
        products:[],
        detailproduct:detailProduct,
        cart:[],
        modalproduct:detailProduct,
    }
    componentDidMount(){
        this.setproduct();
    }

    setproduct=()=>{
        let tempprpduct=[];
        storeProducts.forEach(item=>{
            const single={...item};
            tempprpduct=[...tempprpduct,single]
        });
        this.setState({products:tempprpduct})
    }

    addtocart=id=>{
        let tempproduct=[...this.state.products];
        const index=tempproduct.indexOf(this.getitem(id));
        const product=tempproduct[index];
        product.count=1;
        product.inCart=true;       
        this.setState({products:tempproduct, cart:[...this.state.cart,product]},()=>console.log(this.state))
    }

    getitem=id=>{
        const product=this.state.products.find(item => item.id===id);
        return product;
    }

    handledetail=id=>{
        const product=this.getitem(id);
        this.setState({detailproduct:product})
    }



    openmodal=id=>{
        const product=this.getitem(id);
        this.setState(()=>{return{modalproduct:product,openmodal:true}})
    }

    closemodal=()=>{
        this.setState(()=>{return{openmodal: false}})
    }

    render() {
        return (
            <div>
                <Providerp.Provider value={{...this.state,addtocart:this.addtocart,handledetail:this.handledetail,openmodal:this.openmodal,closemodal:this.closemodal}}>
                    {this.props.children}
                </Providerp.Provider>
            </div>
        );
    }
}

const Consumerp=Providerp.Consumer;

export  {Provider,Consumerp};
