import React from 'react';

const Cartitem = ({value,item}) => {
    const {increment,decrement,remove}=value;
    const {id,image,title,price,total,count}=item;
    return (
            <div className='row justify-contect-between  text-center'>
                <div className="col-lg-2 col-sm-10"><img className="img-fluid" src={image} alt={image}/></div>
                <div className="col-lg-2 col-sm-10"><span className="d-lg-none">Name:</span>{title}</div>
                <div className="col-lg-2 col-sm-10"><span className="d-lg-none">Price:</span>${price}</div>

                <div className="col-lg-2 col-sm-10"><button className="btn btn-info" onClick={()=>decrement(id)}>-</button>
                <span className="btn btn-green">{count}</span>
                <button className="btn btn-info" onClick={()=>increment(id)}>+</button>
                </div>

                <div className="col-lg-2 col-sm-10"><i onClick={()=>remove(id)} className='fas fa-trash' ></i></div>
                <div className="col-lg-2 col-sm-10"><span className="d-lg-none">Item total:</span>{total}</div>
            </div>
       
    );
};

export default Cartitem;
